INSERT INTO attraction_details (attraction_name, attraction_description,attraction_image, city_id) VALUES
("Eiffel Tower", "The Eiffel Tower is arguably the most iconic symbol of Paris and a global landmark.", "https://images.unsplash.com/photo-1511739001486-6bfe10ce785f?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=3087&q=80", 1),
("Notre-Dame Cathedral ", "Notre-Dame Cathedral is a masterpiece of French Gothic architecture and a symbol of Paris. Visitors can admire its stunning facade, and the opportunity to climb to the top for panoramic views of the city. ", "https://images.unsplash.com/photo-1598875793464-d8cc825c32f7?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8M3x8bm90cmUlMjBkYW1lfGVufDB8fDB8fHww&auto=format&fit=crop&w=800&q=60" ,1),
("Louvre Museum", "The Eiffel Tower is arguably the most iconic symbol of Paris and a global landmark. Designed by Gustave Eiffel, it stands 324 meters tall and offers breathtaking views of the city from its observation decks.", "https://images.unsplash.com/photo-1541264161754-445bbdd7de52?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8OHx8bG91dnJlJTIwbXVzZXVtfGVufDB8fDB8fHww&auto=format&fit=crop&w=800&q=60",1),
("Arc de Triomphe", " The Arc de Triomphe is a monumental arch situated at the western end of the Champs-Élysées. It honors those who fought and died for France in the French Revolutionary and Napoleonic Wars.", "https://images.unsplash.com/photo-1569949384963-296c0f251fbc?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=3087&q=80", 1),
